var marker=0;function addSource(b){for(b=b.querySelectorAll(".lred");marker<b.length;marker++){var a=b[marker];a.src=vince(a.getAttribute("data-reblog"),a.getAttribute("data-plink"))+"&name="+a.getAttribute("data-user")+"&avatar="+a.getAttribute("data-pic")}}function vince(b,a){var c="&pid="+b.split("/")[4]+"&rk="+b.split("/")[5];return"http://assets.tumblr.com/assets/html/iframe/o.html?src="+encodeURIComponent(a)+c}
document.addEventListener("DOMContentLoaded",function(){addSource(document.body);(new MutationObserver(function(b){b.forEach(function(a){"childList"===a.type&&addSource(a.target)})})).observe(document.body,{childList:!0,subtree:!0})},!1);