var marker=0;function addSource(a){for(a=a.querySelectorAll(".lred");marker<a.length;marker++){var b=a[marker];b.src=vince(b.getAttribute("data-reblog"),b.getAttribute("data-plink"))}}function vince(a,b){var c="&pid="+a.split("/")[4]+"&rk="+a.split("/")[5];return"http://assets.tumblr.com/assets/html/iframe/o.html?src="+encodeURIComponent(b)+c}
window.addEventListener("DOMContentLoaded",function(){addSource(document.body);(new MutationObserver(function(a){a.forEach(function(a){"childList"===a.type&&addSource(a.target)})})).observe(document.body,{childList:!0,subtree:!0})},!1);